import React from 'react';
import parse from 'html-react-parser'
import useData from 'src/data/shared/useData'
import Link from 'next/link'
function SectionTitle(props) {
    const { news_category } = useData()
    return (
        <div className={`section-title ${props.variant === "light" ? "section-title--light" : ""}`}>
            <If condition={Boolean(news_category)}>
                <Link href={`/[...slug].js`} as={`/${news_category.slug}`}><a><h6>{props.title}</h6></a></Link>    
            </If>
            <If condition={!Boolean(news_category)}>
                <h6>{props.title}</h6>
            </If>
            <h2>{parse(props.heading)}</h2>
            {(() => {
                if (props.text) {
                    return <p>{parse(props.text)}</p>
                }
                if (props.tagline) {
                    return <h5 className="tagline">{parse(props.tagline)}</h5>
                }
            })()}
        </div>
    );
}

export default SectionTitle;
