import React from 'react';
import SectionTitle from "../UI/SectionTitle";
import BlogItem from "./blogItem";

// import Blogs from '../../data/Blog/blog';
import useData from 'src/data/shared/useData'
function Blog() {
    const { cms_articles } = useData()
    
    return (
        <div className="blog-area-wrapper sm-top">
            <div className="container">
                <div className="row">
                    <div className="col-12 text-center">
                        <SectionTitle title="Thông tin liên quan" heading="Cập nhật mới nhất <br> về các văn bản pháp quy"/>
                    </div>
                </div>

                <div className="row mtn-35">
                    {
                        cms_articles.map(blog =>(
                            <BlogItem {...blog} key={blog.id} id={blog.id} title={blog.title} excerpt={blog.intro_text} postBy={blog.author} date={blog.published_at}/>
                        ))
                    }
                </div>
            </div>
        </div>
    );
}

export default Blog;
