import React from 'react';
import Sidebar from "../../components/Sidebar";
import SidebarItem from "../../components/Sidebar/SidebarItem";
import List from "../../components/UI/List";
import LI from "../../components/UI/List/Item";
import Anchor from "../../components/UI/Anchor";
import FeaturedBlog from "../../components/FeaturedBlog";
import Newsletter from "../../components/Newsletter";

import Blogs from "../../data/Blog/blog";
import BannerImg from "../../assets/img/banner-poster.jpg";
import Link from 'next/link'
import useData from 'src/data/shared/useData'
const SidebarForBlog = ({classes}) => {
    const {
        cms_sidebar_groups,
        meta: {
            locale
        }
    } = useData()

    return (
        <Sidebar classes={`col-lg-3 ${classes}`}>
        {
            cms_sidebar_groups.map(sidebar_group => (
                <SidebarItem title={sidebar_group.sidebar_group_name} classes={'single-sidebar-item-wrap'} key={sidebar_group.id}>
                <List classes={'sidebar-list'}>
                    {
                        sidebar_group.sidebar_menus.map(menu=>(
                            <LI key={menu.id}>
                                <>
                                <If condition={menu.is_blank_target}>
                                <a href={menu.slug_sidebar} target="_blank">
                                   {menu.sidebar_name}
                                </a>    
                                </If>
                                <If condition={!menu.is_blank_target}>
                                <a href={`/${menu.slug_sidebar}`}>
                                   {menu.sidebar_name}
                                </a>
                                </If>
                                </>
                            </LI>
                        ))
                    }
                </List>
                </SidebarItem>
            ))
        }
        </Sidebar>
    );
};

export default SidebarForBlog;
