const graphql = async ({
  endpoint = `${process.env.apiUrl}/v1/graphql`,
  query,
  variables = {},
  headers = {
    'x-hasura-admin-secret': process.env.adminSecret
  }
}) => {
  const tmp = await fetch(endpoint, {
    method: 'POST',
    headers,
    body: JSON.stringify({
      query,
      variables
    })
  })
  const tmpJson = await tmp.json()
  return tmpJson.data
}
module.exports = graphql
