import graphql from 'src/data/shared/graphql';
const organizationQuery = `
query($domain:String!) {
  organizations:cms_organizations(
    where:{
      domain:{
        _eq:$domain
      }
    }
  ){
    id
    organization_type {
      id
      organization_type_name
    }
    organization_name
    face
    phone
    zalo
    youtube_channel
    mail
    slug_organization
    address
    logo_url
    twitter
    wiki_url
    domain
  }
}
`;
export const getStaticProps = async () => {
  const variables = {
    domain: process.env.settings.organization_domain
  };
  const {
    organizations: [current_organization, ...rest]
  } = await graphql({
    query: organizationQuery,
    variables
  });
  return {
    props: {
      data: {
        current_organization
      }
    }
  };
};
