import React from 'react';
import Link from 'next/link'

function BlogItem(props) {
    const { img_url, slug } = props
    return (
        <div className={props.cols ? props.cols : 'col-md-6 col-lg-4'}>
            <div className="blog-item">
                {
                    props.thumb ? (
                        <figure className="blog-thumb">
                            <Link href={'/[...slug].js'} as={slug}>
                                <a>
                                <img src={img_url} alt={props.title} />
                                </a>
                            </Link>
                        </figure>
                    ) : null
                }
                <div className="blog-content">
                    <h2 className="h5"><Link href={'/[...slug].js'} as={slug}><a>{props.title}</a></Link></h2>
                    <p dangerouslySetInnerHTML={{
                        __html: props.excerpt
                    }} />

                    <div className="blog-meta">
                       { /* <Link href={'/[...slug].js'} as={slug}><a>By: {props.postBy}</a></Link>
                        <Link href={'/[...slug].js'} as={slug}><a>{props.date}</a></Link> */}
                    </div>
                </div>
            </div>
        </div>
    );
}

export default BlogItem;
