import React from 'react';
import useData from 'src/data/shared/useData'
const getLink = (item, locale) => {
    let itemLink
    if (item.reference) {
        itemLink = (locale === 'vi') ? `/${item.reference.slug}` : `/${locale}/${item.reference.slug}`
    } else {
        itemLink = `/${item.slug}`
    }
    return itemLink
}
function NavbarItem(props) {
    const {
        menus,
        meta: { locale }
    } = useData()

    return(
        menus.map(item=> {
            const hasSubMenu = item.menus.length > 0
            return (
            <li key={item.id}
                className={hasSubMenu ? 'has-submenu' : ''}
            >
                <a href={getLink(item, locale)}>{item.title}</a>
                {(() => {
                    if(hasSubMenu){
                        return(
                            <ul className="submenu-nav">
                                {
                                    item.menus.map((subItem,index)=>{
                                        return <li key={subItem.id}><a href={getLink(subItem, locale)}>{subItem.title}</a></li>
                                    })
                                }
                            </ul>
                        )
                    }
                })()}
            </li>
        )})
    )
}

export default NavbarItem
